import java.math.*;
import java.util.*;

public class Main {
	public static void main(String[] args) {
		BigDecimal a, b, n, m;
		BigDecimal phi = new BigDecimal("1.");
		Scanner in = new Scanner(System.in);
		while (in.hasNext())
		{
			a = in.nextBigDecimal();
			b = in.nextBigDecimal();
			if (a.compareTo(b) < 0)
			{
				n = b.subtract(a);
				m = phi.multiply(n).setScale(0,BigDecimal.ROUND_FLOOR);
				if (a.compareTo(m) == 0) System.out.println('0');
				else System.out.println('1');
			}
			else
			{
				n = a.subtract(b);
				m = phi.multiply(n).setScale(0,BigDecimal.ROUND_FLOOR);
				if (b.compareTo(m) == 0) System.out.println('0');
				else System.out.println('1');
			}
		}
	}
}