int l, r, bl, br;
int a[11], ans;

inline int myabs(int a)
{
	return a < 0 ? -a : a;
}

inline bool magic(int x)
{
	int r = 0, cnt = 0;
	while (x)
	{
		a[cnt++] = x % 10;
		x /= 10;
	}
	if (!cnt) return false;
	if (cnt == 1)
	{
		if (a[0] == 7) return true;
		else return false;
	}
	for (int i = cnt - 2; ~i; i--) r = r * 10 + myabs(a[i + 1] - a[i]);
	return magic(r);
}

int main()
{
	scanf("%d %d", &l, &r);
	bl = (l - 1) / 1000000 + 1;
	br = (r - 1) / 1000000 + 1;
	if (bl == br)
	{
		for (int i = l; i <= r; i++) if (magic(i)) ans++;
		printf("%d\n", ans);
		return 0;
	}
	for (int i = l; i <= bl * 1e6; i++) if (magic(i)) ans++;
	for (int i = bl + 1; i <= br - 1; i++) ans += c[i];
	for (int i = (br - 1) * 1e6 + 1; i <= r; i++) if (magic(i)) ans++;
	printf("%d\n", ans);
	return 0;
}